insert into public.spots (name, type, address, rating, reviews_count, price, tags, trending, img, lat, lng, verified, hours, description) values
  ('Vitality Juice Co', 'Juice Bar', '123 Health Blvd', 4.7, 312, '$$', array['keto-friendly','no-seed-oils','organic','nut-milk-coffee'], 91, 'https://images.unsplash.com/photo-1610970881699-44a5587cabec?w=400&q=80', 33.749, -84.388, true, '7am–8pm', 'Cold-pressed juices, nut milk lattes, and superfood bowls made with 100% organic ingredients. No seed oils ever.'),
  ('Recovery Lab', 'Wellness Center', '321 Performance Dr', 4.7, 76, '$$$$', array['cold-plunge','red-light','sauna','recovery'], 88, 'https://images.unsplash.com/photo-1540555700478-4be289fbecef?w=400&q=80', 33.752, -84.392, true, '6am–10pm', 'Premier recovery facility featuring cold plunge pools, infrared saunas, red light therapy, and float tanks.'),
  ('Green Soul Kitchen', 'Restaurant', '245 Wellness Ave', 4.8, 234, '$$$', array['plant-based','organic','no-seed-oils','gluten-free'], 85, 'https://images.unsplash.com/photo-1512621776951-a57141f2eefd?w=400&q=80', 33.746, -84.385, true, '11am–9pm', 'Farm-to-table plant-based cuisine. Everything made in-house with organic produce from local farms.'),
  ('Keto Kitchen Lab', 'Restaurant', '789 Protein Pl', 4.4, 167, '$$', array['keto-friendly','carnivore','no-seed-oils','high-protein'], 82, 'https://images.unsplash.com/photo-1467003909585-2f8a72700288?w=400&q=80', 33.755, -84.379, true, '10am–9pm', 'Strict keto meals, grass-fed meats, zero seed oils. Full macros listed on every item.'),
  ('Elevate Pilates', 'Fitness Studio', '512 Core St', 4.9, 445, '$$$', array['pilates','reformer','barre','low-impact'], 79, 'https://images.unsplash.com/photo-1518611012118-696072aa579a?w=400&q=80', 33.743, -84.391, true, '6am–8pm', 'Top-rated reformer pilates studio. 12 instructors, 40+ weekly classes. Beginner to advanced.'),
  ('Functional Roots', 'Health Store', '88 Supplement Blvd', 4.5, 189, '$$', array['supplements','adaptogens','organic','biohacking'], 74, 'https://images.unsplash.com/photo-1505576399279-565b52d4ac71?w=400&q=80', 33.758, -84.382, true, '9am–7pm', 'Curated supplements, adaptogens, and functional foods. Staff are all certified nutritionists.'),
  ('The Nut Lab', 'Café', '44 Barista Lane', 4.6, 203, '$$', array['nut-milk-coffee','organic','plant-based','gluten-free'], 71, 'https://images.unsplash.com/photo-1511920170033-f8396924c348?w=400&q=80', 33.751, -84.395, true, '7am–6pm', 'House-made oat, macadamia, and pistachio milk lattes. Speciality coffee sourced from regenerative farms.'),
  ('Zone Fitness Academy', 'Gym', '200 Zone 2 Way', 4.5, 312, '$$', array['zone-2-cardio','strength','crossfit','hiit'], 68, 'https://images.unsplash.com/photo-1571902943202-507ec2618e8f?w=400&q=80', 33.744, -84.387, true, '5am–11pm', 'Specialized programming built around Zone 2 cardio principles with strength integration.');
insert into public.trends (name, category, rating, spots_count, badge, hot, img, description, rise, difficulty, cost) values
  ('Cold Plunge Therapy', 'recovery', 4.3, 15, 'Science-backed', true, 'https://images.unsplash.com/photo-1548438294-1ad5d5f4f063?w=600&q=80', 'Deliberate cold exposure through ice baths or cold plunges to boost dopamine, reduce inflammation, and improve recovery. The most-searched wellness trend of 2025.', '+240%', 'Moderate', '$$'),
  ('Adaptogenic Mushrooms', 'supplements', 4.5, 18, 'Science-backed', true, 'https://images.unsplash.com/photo-1504328345606-18bbc8c9d7d1?w=600&q=80', 'Functional mushrooms like lion''s mane, reishi, cordyceps, and chaga used for cognitive enhancement, stress reduction, and immune support.', '+185%', 'Easy', '$'),
  ('Zone 2 Cardio', 'fitness', 4.4, 22, 'Science-backed', true, 'https://images.unsplash.com/photo-1476480862126-209bfaa8edc8?w=600&q=80', 'Low-intensity steady-state cardio at 60-70% max heart rate. Builds mitochondrial density and metabolic efficiency without cortisol spike.', '+162%', 'Easy', '$'),
  ('Red Light Therapy', 'biohacking', 4.2, 14, 'Science-backed', true, 'https://images.unsplash.com/photo-1512290923902-8a9f81dc236c?w=600&q=80', 'Red and near-infrared wavelengths to promote cellular regeneration, collagen production, and reduce inflammation.', '+145%', 'Easy', '$$$'),
  ('Seed Cycling', 'nutrition', 4.1, 8, 'Emerging', true, 'https://images.unsplash.com/photo-1490645935967-10de6ba17061?w=600&q=80', 'Rotating seed consumption (flax, pumpkin, sesame, sunflower) to naturally support hormonal balance throughout the month.', '+128%', 'Easy', '$'),
  ('No Seed Oil Diet', 'nutrition', 4.6, 31, 'Trending', false, 'https://images.unsplash.com/photo-1474979266404-7eaacbcd87c5?w=600&q=80', 'Eliminating industrial seed oils (canola, soybean, corn) in favor of traditional fats like beef tallow, butter, and olive oil.', '+310%', 'Moderate', '$$'),
  ('Carnivore Diet', 'nutrition', 4.0, 12, 'Controversial', false, 'https://images.unsplash.com/photo-1529692236671-f1f6cf9683ba?w=600&q=80', 'Animal-only diet consisting primarily of red meat, organs, and animal fats. Proponents report reduced inflammation and mental clarity.', '+195%', 'Hard', '$$$'),
  ('Reformer Pilates', 'fitness', 4.8, 27, 'Mainstream', false, 'https://images.unsplash.com/photo-1518611012118-696072aa579a?w=600&q=80', 'Spring-resistance machine pilates that builds core strength, flexibility, and postural alignment. Exploded in popularity in 2024-2025.', '+220%', 'Moderate', '$$$');
